import React, { Component } from 'react';
import './App.css';
import { FormGroup, FormControl, InputGroup, Glyphicon } from 'react-bootstrap';

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      query:''
    }
  }

search(){
  console.log('this.state', this.state);
  const BASE_URL = 'https://api.spotify.com/v1/search?';
  const FETCH_URL = BASE_URL + "q=" +this.state.query+ "&type=artist&limit=1";
//  const FETCH_URL = ${BASE_URL}q=${this.state.query}&type=artist&limit=1;
  console.log(FETCH_URL);
// eslint-disable-next-line
  fetch(FETCH_URL, {
    method: 'GET',
    headers: {
      'Authorization': 'Bearer BQDJ-CcMCBO_thzwNww-5bhDy7wjSg2UBhdZG-4oXypHDN_JUbj26C2VQxRaAoVTNPUZWgCET5Xq-kwZyRqMzx3YRmGtW8Fp8TfXSHJ7Cj5fztWvdOWXdnqAd3hU_LMq0HyPIJqVDifuthD9af9Rp2BtyQKrXA4xvkSKTbUP'
    }
  })
  .then(response => response.json())
  .then(json => {
    console.log('json', json)
    const artist = json.artists.items[0];
    console.log('artist', artist);
  }
  );
}

  render(){
    return(
      <div className="app">
        <div className="app-title">Music Master</div>
        <FormGroup>
        <InputGroup>
          <FormControl
          placeholder="search an artist..."
          value={this.state.query}
          onChange={event=>{this.setState({query:event.target.value})}}
          onKeyPress={
            // event=>console.log('event.key', event.key)
            event=>{
              if(event.key==='Enter'){
                this.search()
              }
            }
          }
            />
            <InputGroup.Addon onClick={()=>this.search()}>
            <Glyphicon glyph="search"></Glyphicon>

          </InputGroup.Addon>
          </InputGroup>
        </FormGroup>
        <div className="profile">
          <div>artist picture</div>
          <div>artist name</div>
        </div>
        <div className="gallery">
        gallery
        </div>
      </div>
    )
  }
}

export default App;
